mapper.select:
select

from mdl_approved
where 1=1

  and request_date_time=#{requestDateTime,jdbcType=TIMESTAMP}


  and requestor_user_guid=#{requestorUserGuid,jdbcType=VARCHAR}


  and `function`=#{function,jdbcType=VARCHAR}


  and identifier=#{identifier,jdbcType=VARCHAR}


  and `status`=#{status,jdbcType=VARCHAR}


  and approved_date_Time=#{approvedDateTime,jdbcType=TIMESTAMP}


  and approver_user_guid=#{approverUserGuid,jdbcType=VARCHAR}


  and before_data=#{beforeData,jdbcType=LONGVARCHAR}


  and after_data=#{afterData,jdbcType=LONGVARCHAR}


  and remark=#{remark,jdbcType=VARCHAR}


  and create_by=#{createBy,jdbcType=VARCHAR}


  and last_modified_by=#{lastModifiedBy,jdbcType=VARCHAR}


  and id=#{id,jdbcType=INTEGER}

select

from mdl_approved
where 1=1

  and request_date_time=#{requestDateTime,jdbcType=TIMESTAMP}


  and requestor_user_guid=#{requestorUserGuid,jdbcType=VARCHAR}


  and `function`=#{function,jdbcType=VARCHAR}


  and identifier=#{identifier,jdbcType=VARCHAR}


  and `status`=#{status,jdbcType=VARCHAR}


  and approved_date_Time=#{approvedDateTime,jdbcType=TIMESTAMP}


  and approver_user_guid=#{approverUserGuid,jdbcType=VARCHAR}


  and before_data=#{beforeData,jdbcType=LONGVARCHAR}


  and after_data=#{afterData,jdbcType=LONGVARCHAR}


  and remark=#{remark,jdbcType=VARCHAR}


  and create_by=#{createBy,jdbcType=VARCHAR}


  and last_modified_by=#{lastModifiedBy,jdbcType=VARCHAR}


  and create_date=#{createDate,jdbcType=TIMESTAMP}


  and last_modified_date=#{lastModifiedDate,jdbcType=TIMESTAMP}


  and id=#{id,jdbcType=INTEGER}

select

from mdl_approved mat left join mdl_approved_function_setting mafst on  mat.`function` = mafst.`function_code`
where 1=1

  and mat.request_date_time=#{requestDateTime,jdbcType=TIMESTAMP}


  and mat.requestor_user_guid=#{requestorUserGuid,jdbcType=VARCHAR}


  and mat.`function`=#{function,jdbcType=VARCHAR}


  and mat.identifier=#{identifier,jdbcType=VARCHAR}


  and mat.`status`=#{status,jdbcType=VARCHAR}


  and mat.approved_date_Time=#{approvedDateTime,jdbcType=TIMESTAMP}


  and mat.approver_user_guid=#{approverUserGuid,jdbcType=VARCHAR}


  and mat.before_data=#{beforeData,jdbcType=LONGVARCHAR}


  and mat.after_data=#{afterData,jdbcType=LONGVARCHAR}


  and mat.remark=#{remark,jdbcType=VARCHAR}


  and mat.create_by=#{createBy,jdbcType=VARCHAR}


  and mat.last_modified_by=#{lastModifiedBy,jdbcType=VARCHAR}


