mapper.update:
UPDATE mdl_audit_log
create_by= #{auditLogInfo.createBy,jdbcType=VARCHAR}, last_modified_by= #{auditLogInfo.lastModifiedBy,jdbcType=VARCHAR},
create_date= #{auditLogInfo.createDate,jdbcType=TIMESTAMP}, last_modified_date= #{auditLogInfo.lastModifiedDate,jdbcType=TIMESTAMP},
operation_type= #{auditLogInfo.operationType,jdbcType=VARCHAR},
title= #{auditLogInfo.title,jdbcType=VARCHAR}, service_id= #{auditLogInfo.serviceId,jdbcType=VARCHAR}, remote_addr= #{auditLogInfo.remoteAddr,jdbcType=VARCHAR}, user_agent= #{auditLogInfo.userAgent,jdbcType=VARCHAR}, request_uri= #{auditLogInfo.requestUri,jdbcType=VARCHAR}, method= #{auditLogInfo.method,jdbcType=VARCHAR}, params= #{auditLogInfo.params,jdbcType=VARCHAR}, time= #{auditLogInfo.time,jdbcType=VARCHAR}, del_flag= #{auditLogInfo.delFlag,jdbcType=INTEGER}, exception= #{auditLogInfo.exception,jdbcType=VARCHAR}
WHERE id = #{auditLogInfo.id,jdbcType=BIGINT}

mapper.select:
select

from mdl_audit_log
where 1=1

    and operation_type=#{operationType,jdbcType=VARCHAR}


    and title=#{title,jdbcType=VARCHAR}


    and service_id=#{serviceId,jdbcType=VARCHAR}


    and remote_addr=#{remoteAddr,jdbcType=VARCHAR}


    and user_agent=#{userAgent,jdbcType=VARCHAR}


    and request_uri=#{requestUri,jdbcType=VARCHAR}


    and `method`=#{method,jdbcType=VARCHAR}


    and params=#{params,jdbcType=VARCHAR}


    and `time`=#{time,jdbcType=VARCHAR}


    and del_flag=#{delFlag,jdbcType=INTEGER}


    and `exception`=#{exception,jdbcType=VARCHAR}


    and create_by=#{createBy,jdbcType=VARCHAR}


    and last_modified_by=#{lastModifiedBy,jdbcType=VARCHAR}


    and create_date=#{createDate,jdbcType=TIMESTAMP}


    and last_modified_date=#{lastModifiedDate,jdbcType=TIMESTAMP}


    and id=#{id,jdbcType=BIGINT}
