mapper.select:
select

from mdl_lov_value
where 1=1

    and lov_id=#{lovId,jdbcType=BIGINT}


    and parent_id=#{parentId,jdbcType=BIGINT}


    and code=#{code,jdbcType=VARCHAR}


    and `name`=#{name,jdbcType=VARCHAR}


    and description=#{description,jdbcType=VARCHAR}


    and `locale`=#{locale,jdbcType=VARCHAR}


    and dis_flag=#{disFlag,jdbcType=BOOLEAN}


    and del_flag=#{delFlag,jdbcType=BOOLEAN}


    and tenant_id=#{tenantId,jdbcType=INTEGER}


    and create_by=#{createBy,jdbcType=VARCHAR}


    and last_modified_by=#{lastModifiedBy,jdbcType=VARCHAR}


    and create_date=#{createDate,jdbcType=TIMESTAMP}


    and last_modified_date=#{lastModifiedDate,jdbcType=TIMESTAMP}


    and id=#{id,jdbcType=BIGINT}



        and parent_id is null

    AND EXISTS (SELECT 1 FROM mdl_lov mlt WHERE mlt.id = mdl_lov_value.lov_id AND mlt.code = #{lovCode})

select count(1)
from mdl_lov_value
where id=#{id,jdbcType=BIGINT}

