mapper.select:
select count(1)
from mdl_user_history
where update_type=#{updateType,jdbcType=VARCHAR} and guid=#{guid,jdbcType=BIGINT} and last_modified_date > #{minLastModifiedDate,jdbcType=TIMESTAMP}

select

from mdl_user_history
where 1=1

    and id=#{id,jdbcType=BIGINT}


    and update_type=#{updateType,jdbcType=VARCHAR}


    and guid=#{guid,jdbcType=BIGINT}


    and username=#{username,jdbcType=VARCHAR}


    and cellphone=#{cellphone,jdbcType=VARCHAR}


    and email=#{email,jdbcType=VARCHAR}


    and nickname=#{nickname,jdbcType=VARCHAR}


    and `password`=#{password,jdbcType=VARCHAR}


    and default_role=#{defaultRole,jdbcType=VARCHAR}


    and default_locale=#{defaultLocale,jdbcType=VARCHAR}


    and account_effective_date=#{accountEffectiveDate,jdbcType=TIMESTAMP}


    and credentials_effective_date=#{credentialsEffectiveDate,jdbcType=TIMESTAMP}


    and `status`=#{status,jdbcType=VARCHAR}


    and locked=#{locked,jdbcType=TINYINT}


    and lock_limit=#{lockLimit,jdbcType=TINYINT}


    and lock_time=#{lockTime,jdbcType=TIMESTAMP}


    and last_login_time=#{lastLoginTime,jdbcType=TIMESTAMP}


    and last_login_ip=#{lastLoginIp,jdbcType=VARCHAR}


    and referral=#{referral,jdbcType=VARCHAR}


    and create_by=#{createBy,jdbcType=VARCHAR}


    and create_date=#{createDate,jdbcType=TIMESTAMP}


    and last_modified_by=#{lastModifiedBy,jdbcType=VARCHAR}


    and last_modified_date=#{lastModifiedDate,jdbcType=TIMESTAMP}

